package day_2;

public class color {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String colour="Orange";
		switch(colour){
			case "red":
				System.out.println("Holi");
				break;
			case "green":
				System.out.println("Ramzan");
				break;
			case "White":
				System.out.println("Chritsmas");
				break;
			default:
				System.out.println("None");
				break;	
		}

	}

}
